import React, { useEffect, useState } from "react";

function Timer(setStop,questionNumber) {
  const [timer, setTimer] = useState(30);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer((prev) => prev - 1);
    },1000);
  });
  return timer;
}

export default Timer;
