package com.voting.test.enums;

public class VoteIn {
	int vid;
	int pid;
	public VoteIn() {
		super();
		// TODO Auto-generated constructor stub
	}
	public VoteIn(int vid, int pid) {
		super();
		this.vid = vid;
		this.pid = pid;
	}
	public int getVid() {
		return vid;
	}
	public void setVid(int vid) {
		this.vid = vid;
	}
	public int getPid() {
		return pid;
	}
	public void setPid(int pid) {
		this.pid = pid;
	}
	

}
